    private void loginActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_loginActionPerformed
        DB db = new DB("BancoDados.db");
        
        var emailUsuario = txtEmail.getText();
        var senhaUsuario = txtSenha.getText();
        
        var query = "SELECT * from Usuario where Senha = ";
        query = query + "'" + senhaUsuario + "' and email = '";
        query = query + emailUsuario + "';";
        db.query(query);
        var email = db.getString("email");
        var senha = db.getString("Senha");
        var codigo = db.getString("Codigo");
        
        System.out.println(emailUsuario);
        System.out.println(senhaUsuario);
        System.out.println(email);
        System.out.println(senha);
        
        if(email == null) {
            email = "";
        }
        
        if(senha == null) {
            senha = "";
        }
        
        if(emailUsuario.contentEquals(email) && senhaUsuario.contentEquals(senha)) {
            System.out.println(abrirAnimais.getState());
            if (abrirAnimais.getState() == true){
                Animais animais = new Animais();
                animais.setVisible(true);
            }
            
            if (abrirCarros.getState() == true){
                Carros carros = new Carros();
                carros.setVisible(true);
            }
            
            if (abrirEditarUser.getState() == true){
                Usuarios usuarios = new Usuarios(Integer.parseInt(codigo));
                usuarios.setVisible(true);
            }
            
        }
        else {
            System.out.println("Login inválido");
        }
        
        this.dispose();
        db.closeConnection();
    }//GEN-LAST:event_loginActionPerformed

