    private void loginAdmActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_loginAdmActionPerformed
        DB db = new DB("BancoDados.db");
        
        var emailUsuario = txtEmail.getText();
        var senhaUsuario = txtSenha.getText();
        
        var query = "SELECT * from Usuario where Senha = ";
        query = query + "'" + senhaUsuario + "' and email = '";
        query = query + emailUsuario + "';";
        System.out.println(query);
        db.query(query);
        var email = db.getString("email");
        var senha = db.getString("Senha");
        //var codigo = db.getString("Codigo");
        
        System.out.println(emailUsuario);
        System.out.println(senhaUsuario);
        System.out.println(email);
        System.out.println(senha);
        
        if(email == null) {
            email = "";
        }
        
        if(senha == null) {
            senha = "";
        }
        
        if(emailUsuario.contentEquals("teste@ADM.com") && senhaUsuario.contentEquals("ADM123")) {

            Animais animaisADM = new Animais();
            animaisADM.setVisible(true);

            Carros carrosADM = new Carros();
            carrosADM.setVisible(true);
            
            Usuarios usuariosADM = new Usuarios();
            usuariosADM.setVisible(true);
            
            
        }
        
        else{
            System.out.println("Você não é Administrador");
        }
        this.dispose();
        db.closeConnection();
    }//GEN-LAST:event_loginAdmActionPerformed

